    { titular:'Juan Perez', numeroTarjeta: '1565478954983012', fechaExpiracion: '11/12', cvv: '889' },
    { titular:'Miguel Sanchez', numeroTarjeta: '1203564897564123', fechaExpiracion: '09/18', cvv: '513' },
  ];

  //Creamos una nuevaa varialble de tipo FormGroup
  form: FormGroup;

  constructor(private fb:FormBuilder) {
    this.form = this.fb.group({
      titular: [''],
      numeroTarjeta: [''],
      fechaExpiracion: [''],
      cvv: ['']
  })
  }

  ngOnInit(): void {

  }
}
