import { Component, OnInit } from '@angular/core';
//Importamos FormGroup, FormBuilder y Validators
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
//Importamos ToastrService
import { ToastrService } from 'ngx-toastr';
import { TarjetaService } from 'src/app/services/tarjeta.service';

@Component({
  //Nombre que tenemos que poner en app-ComponentFactoryResolver.html para que lo renderice
  selector: 'app-tarjeta-credito',
  templateUrl: './tarjeta-credito.component.html',
  styleUrls: ['./tarjeta-credito.component.css']
})
export class TarjetaCreditoComponent {
  //Creamos array de objetos para iterarlo
  listTarjetas: any[] = [];

  accion = 'Agregar';

  id: number | undefined;

  //Creamos una nuevaa varialble de tipo FormGroup
  form: FormGroup;
//Inyectamos las dependencias en el contructor
  constructor(private fb:FormBuilder,
    private toastr: ToastrService,
    private _tarjetaService: TarjetaService) {
    this.form = this.fb.group({
  //Agregamos las validaciones
      titular: ['', Validators.required],
  //Si es mas de 1 validacion debe ir entre corchetes
      numeroTarjeta: ['', [Validators.required, Validators.maxLength(16), Validators.minLength(16)]],
      fechaExpiracion: ['', [Validators.required, Validators.maxLength(5), Validators.minLength(5)]],
      cvv: ['', [Validators.required, Validators.maxLength(3), Validators.minLength(3)]]
  })
  }

  ngOnInit(): void {
    this.obtenerTarjetas();
  }

  obtenerTarjetas() {
    this._tarjetaService.getListTarjetas().subscribe(data =>{
      console.log(data);
      this.listTarjetas = data;
    }, error => {
      console.log(error);
    })
  }

  agregarTarjeta() {

    const tarjeta: any = {
      titular: this.form.get('titular')?.value,
      numeroTarjeta: this.form.get('numeroTarjeta')?.value,
      fechaExpiracion: this.form.get('fechaExpiracion')?.value,
      cvv: this.form.get('cvv')?.value
    }
    this._tarjetaService.saveTarjeta(tarjeta).subscribe(data => {
      this.toastr.success('La tarjeta fue registrada con exito', 'Tarjeta agregada');
      this.obtenerTarjetas();
      this.form.reset();
    }, error => {
      this.toastr.error('Opss... ocurrio un error', 'Error');
      console.log(error);
    })
  }

  eliminarTarjeta(id: number) {
    this._tarjetaService.deleteTarjeta(id).subscribe(data => {
      this.toastr.error('La tajeta fue eliminada con exito', 'Tarjeta eliminada');
      this.obtenerTarjetas();
    }, error => {
      console.log(error);
    })
  }

  editarTarjeta(tarjeta: any) {
    this.accion = 'Editar';
    this.id = tarjeta.id;
    this.form.patchValue({
      titular: tarjeta.titular,
      numeroTarjeta: tarjeta.numeroTarjeta,
      fechaExpiracion: tarjeta.fechaExpiracion,
      cvv: tarjeta.cvv
    })
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-8 offset-lg-2">
            <div class="card">
                <div class="card-body">
                    <h3 class="titulo">TARJETA CREDITO</h3>
                </div>
            </div>
        </div>
    </div>

    <div class="row mt-4">
        <div class="col-lg-6">
            <div class="card">
                <div class="card-body">
                    <h5 class="titulo">{{ accion | uppercase }} TARJETA</h5>
        <!--Vinculamos el formulario html con el constructor form y le agregamos una funcion-->
                    <form [formGroup]="form" (ngSubmit)="agregarTarjeta()">
                        <div class="input-group input-group-lg mb-3">
                            <span class="input-group-text">
                                <i class="fa-solid fa-user"
                                [class.green-icon]="this.form.get('titular')?.valid"
                                [class.red-icon]="this.form.get('titular')?.invalid && this.form.get('titular')?.touched"></i>
                        <!--Le decimos que use la clase green-icon cuando sea valido => pase las validaciones
                        y la clase red-icon cuando sea invalido y haya sido tocado-->
                            </span>
                            <input formControlName="titular" type="text" class="form-control" placeholder="Titular">
                        </div>
                        <div class="input-group input-group-lg mb-3">
                            <span class="input-group-text">
                                <i class="fa-solid fa-credit-card"
                                [class.green-icon]="this.form.get('numeroTarjeta')?.valid"
                                [class.red-icon]="this.form.get('numeroTarjeta')?.invalid && this.form.get('numeroTarjeta')?.touched"></i>
                            </span>
                        <!--Le damos a los inputs la propiedad maxlength para que no deje escribir mas-->
                            <input formControlName="numeroTarjeta" maxlength="16" type="text" class="form-control" placeholder="Numero de tarjeta">
                        </div>
                        <div class="row">
                            <div class="col-lg-7">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">
                                        <i class="fa-solid fa-calendar-days"
                                        [class.green-icon]="this.form.get('fechaExpiracion')?.valid"
                                        [class.red-icon]="this.form.get('fechaExpiracion')?.invalid && this.form.get('fechaExpiracion')?.touched"></i>
                                    </span>
                                    <input formControlName="fechaExpiracion" maxlength="5" type="text" class="form-control" placeholder="MM/YY">
                                </div>
                            </div>
                            <div class="col-lg-5">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">
                                        <i class="fa-solid fa-key"
                                        [class.green-icon]="this.form.get('cvv')?.valid"
                                        [class.red-icon]="this.form.get('cvv')?.invalid && this.form.get('cvv')?.touched"></i>
                                    </span>
                                    <input formControlName="cvv" maxlength="3" type="password" class="form-control" placeholder="CVV">
                                </div>
                            </div>
                        </div>
                        <div class="d-grid gap-2">
                            <button [disabled]="this.form.invalid" type="submit" class="btn btn-success btn-lg">
                                <i class="fa-solid fa-database"></i>&nbsp; Aceptar</button>                                
                        </div>
                    </form>
                </div>
            </div>
        </div>
        <div class="col-lg-6">
            <div class="card">
                <div class="card-body">
                    <h5 class="titulo">LISTADO DE TARJETAS</h5>
                    <table class="table">
                        <tbody>
                    <!--Iteramos directamente en la etiqueta tr y le damos un indice para eliminar y editar los registros-->
                            <tr *ngFor="let tarjeta of listTarjetas">
                                <td>{{ tarjeta.titular }}</td>
                                <td>{{ tarjeta.numeroTarjeta }}</td>
                                <td>{{ tarjeta.fechaExpiracion }}</td>
                                <td>
                                    <i (click)="editarTarjeta(tarjeta)" class="far fa-solid fa-pen-to-square text-info"></i>
                                </td>
                                <td>
                                    <i (click)="eliminarTarjeta(tarjeta.id)" class="far fa-sharp fa-solid fa-trash text-danger"></i>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    </div>
</div>
